import React, { useState } from "react";

import { SIGNIN, CHANGEPASS, REGISTER } from "./AuthMode";
import "./Auth.css";
import "../../shared/style/common.css";

const Auth = ({authmode}) => {
  const [mode, setMode] = useState({authmode});
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleSubmit = (e) => {
    console.log("You just clicked the login button. Congratulations. You've been rejected and will remain anonymous forever. :)")
    e.preventDefault();
  };

  const handleRegister = (e) => {
    console.log("Clicked sign up link");
    e.preventDefault();
  };

  const renderSignin = () => {
    return (
      <React.Fragment>
        <h3 className="login-form-title">Sign in</h3>
        <div className="text-center">
          Not registered?{" "}
          <span className="link-primary" onClick={signUp}>Sign up</span>
        </div>
        <div className="text-center form-group mt-3">
          <label>
            Username
            <input 
              type="text" 
              value={username} 
              className="form-control mt-1"
              placeholder="Enter your username..."
              onChange={(e) => setUsername(e.target.value)}/>
          </label>
        </div>
        <div className="text-center form-group mt-3">
          <label>
            Password
            <input 
              type="password" 
              value={password} 
              className="form-control mt-1"
              placeholder="Enter your password..."
              onChange={(e) => setPassword(e.target.value)}/>
          </label>
        </div>      
        <div className="text-center mt-3">
          <button type="submit" className="login-button" onClick={handleSubmit}>
            Login
          </button>
        </div>      
        <div>
          <p className="text-center mt-2">
            Forgot <span className="link-primary" onClick={setMode(CHANGEPASS)}>password?</span>
          </p>
        </div>
        <div>
          <p className="text-center mt-2">
            <span className="link-primary" onClick={setMode(REGISTER)}>Register</span>
          </p>
        </div>
      </React.Fragment>
    )
  };

  const renderRegister = () => {
    return (
      <React.Fragment>
        <div className="login-container">
          <form className="login-form">
            <div className="login-form-content">
              <h3 className="login-form-title">Sign In</h3>
              <div className="text-center">
                Already registered?{" "}
                <span className="link-primary" onClick={setMode(SIGNIN)}>
                  Sign In
                </span>
              </div>
              <div className="form-group mt-3">
                <label>Username</label>
                <input
                  type="text"
                  value={username}
                  className="form-control mt-1"
                  placeholder="Enter the username you want..."
                  onChange={(e) => setUsername(e.target.value)}
                />
              </div>
              <div className="form-group mt-3">
                <label>Email address</label>
                <input
                  type="email"
                  className="form-control mt-1"
                  placeholder="Email address..."
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              <div className="form-group mt-3">
                <label>Password</label>
                <input
                  type="password"
                  className="form-control mt-1"
                  placeholder="Password..."
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>
              <div className="form-group mt-3">
                <label>Confirm Password</label>
                <input 
                  type="password"
                  className="form-control mt-1"
                  placeholder="Confirm password..."
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
              </div>                                                
              <div className="d-grid gap-2 mt-3">
                <button type="submit" className="login-button">
                  Register
                </button>
              </div>
              <p className="text-center mt-2">
                Forgot <span onClick={setMode(CHANGEPASS)}>password?</span>
              </p>
            </div>
          </form>
        </div>
      </React.Fragment>
    )
  };

  const renderChangePass = () => {
    return (
      <div>
      </div>
    );
  };

  switch (mode) {
    case SIGNIN:
      return renderSignin;
    case REGISTER:
      return renderRegister;
    case CHANGEPASS:
      return renderChangePass;
    default:
      return renderSignin;
  }
};

export default Auth;
