import React, { useState } from "react";

import { CUISINE, SIDE, DRINK, DESSERT } from "./MenuSection";
import "./Menu.css";

const Menu = ({menuSectionProp}) => {
  const [menuSectionState, setMenuSectionState] = useState(menuSectionProp);

  const menuSections = [
    CUISINE,
    SIDE,
    DRINK,
    DESSERT
  ];

  const renderedMenuSections = menuSections.map((menuSection) => {
    return (
      <span 
        key={menuSection} 
        onClick={() => setMenuSectionState(menuSection)} 
        className={"menu-section-item " + menuSection === menuSectionState ? "menu-section-item menu-section-selected" : ""}>
        {menuSection}
      </span>
    );
  });

  return (
    <div className="menu-container">
      <div className="menu-section">
          {renderedMenuSections}
      </div>
      <div>
        <h1>{menuSectionState}</h1>
      </div>
    </div>
  );
};

Menu.defaultProps = {
  menuSectionProp: CUISINE
};

export default Menu;
