import React, { useState } from "react";

import "./Login.css";
import "../../shared/style/common.css";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    console.log("You just clicked the login button. Congratulations. You've been rejected and will remain anonymous forever. :)")
    e.preventDefault();
  };

  const signUp = (e) => {
    console.log("Clicked sign up link");
    e.preventDefault();
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h3 className="login-form-title">Sign in</h3>
        <div className="sign-up">
          Not registered?{" "}
          <span className="link-primary" onClick={signUp}>Sign up</span>
        </div>
        <div className="form-group mt-3">
          <label>
          Username:
          <input 
            type="text" 
            value={username} 
            placeholder="Enter your username..."
            onChange={(e) => setUsername(e.target.value)}/>
        </label>
        </div>

        <label>
          Password:
          <input 
            type="password" 
            value={password} 
            placeholder="Enter your password..."
            onChange={(e) => setPassword(e.target.value)}/>
        </label>
        <input type="submit" value="Login"/>
      </form>
    </div>
  );
};

export default Login;
