import React, { useState } from "react";

import "./LoginAndRegister.css";
import "../../shared/style/common.css";

const LoginAndRegister = ({authmode}) => {
  const [mode, setMode] = useState({authmode});
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    console.log("You just clicked the login button. Congratulations. You've been rejected and will remain anonymous forever. :)")
    e.preventDefault();
  };

  const signUp = (e) => {
    console.log("Clicked sign up link");
    e.preventDefault();
  };

  const changeMode = () => {
    setMode(authmode === "sign-in" ? "sign-up" : "sign-in");
  };

  if (authmode === "sign-in") {
    return (
      <div className="login-container">
        <form className="login-form" onSubmit={handleSubmit}>
          <h3 className="login-form-title">Sign in</h3>
          <div className="text-center">
            Not registered?{" "}
            <span className="link-primary" onClick={signUp}>Sign up</span>
          </div>
          <div className="text-center form-group mt-3">
            <label>
              Username
              <input 
                type="text" 
                value={username} 
                className="form-control mt-1"
                placeholder="Enter your username..."
                onChange={(e) => setUsername(e.target.value)}/>
            </label>
          </div>
          <div className="text-center form-group mt-3">
            <label>
              Password
              <input 
                type="password" 
                value={password} 
                className="form-control mt-1"
                placeholder="Enter your password..."
                onChange={(e) => setPassword(e.target.value)}/>
            </label>
          </div>      
          <div className="text-center mt-3">
            <button type="submit" className="login-button">
              Login
            </button>
          </div>      
          <div>
            <p className="text-center mt-2">
              Forgot <a className="link-primary" href="#">password?</a>
            </p>
          </div>
          <div>
            <p className="text-center mt-2">
              <span className="link-primary" onClick={changeMode}>Register</span>
            </p>
          </div>
        </form>
      </div>
    );
  } else {

  }
};

export default LoginAndRegister;
