import React, { useState } from "react";
import { Button } from "react-bootstrap";

const Cart = () => {
  const [items, setItems] = useState([]);
  const [cart, setCart] = useState([]);

  setItems([
      {
        id: 1,
        itemName: "Cornbread",
        itemPrice: 3,
        imgSrc: ""
      },
      {
        id: 2,
        itemName: "Grilled corn",
        itemPrice: 4,
        imgSrc: ""
      }
  ]);

  setCart([
    {
      id: 1,
      itemId: 1,
      counter: 3
    },
    {
      id: 2,
      itemId: 2,
      counter: 2
    }
  ]);

  const findCartItemCounter = (item) => {
    return cart.find((cartItem) => item.id === cartItem.itemId).counter;
  };

  const renderedItems = items.map((item) => {
    return (
      <div key={item.id} className="item pad">
        <div className="img-box">
          <img src={item.imgSrc} alt=""/>
        </div>
        <div className="about">
          <h1 className="title">{item.itemName}</h1>
        </div>
        <div className="counter">
          <Button className="btn">+</Button>
          <div className="counter">{findCartItemCounter(item)}</div>
           <Button className="btn">-</Button>
        </div>
      </div>
    );
  });

  return (
    <div className="page-body">
      <div className="container">
        <div className="header">
          <h3 className="heading">Shopping Cart</h3>
          <h5 className="action">Remove All</h5>
        </div>
        {renderedItems}
      </div>
    </div>
  );
};

export default Cart;
