import React, { useState } from "react";

import { MENU_CUISINE, MENU_SIDE, MENU_DRINK, MENU_DESSERT } from "./MenuSection";
import "./Menu.css";

const Menu = ({menuSectionProp}) => {
  const [menuSectionState, setMenuSectionState] = useState(menuSectionProp);

  const menuSections = [
    MENU_CUISINE,
    MENU_SIDE,
    MENU_DRINK,
    MENU_DESSERT
  ];

  const renderedMenuSections = menuSections.map((menuSection) => {
    return (
      <li className="menu-section-item">
        {section}
      </li>
    );
  });

  return (
    <div className="menu-container">
      <div className="menu-section">
        <ul>
          {renderedMenuSections}
        </ul>
      </div>
    </div>
  );
};

Menu.defaultProps = {
  menuSection: MENU_CUISINE
};

export default Menu;
