import React, { useState } from "react";

import { MENU_CUISINE, MENU_SIDE, MENU_DRINK, MENU_DESSERT } from "./MenuSection";
import "./Menu.css";

const Menu = ({menuSectionProp}) => {
  const [menuSectionState, setMenuSectionState] = useState(menuSectionProp);

  const menuSections = [
    MENU_CUISINE,
    MENU_SIDE,
    MENU_DRINK,
    MENU_DESSERT
  ];

  const handleMenuSectionClick = (menuSection) => {
    setMenuSectionState(menuSection);
  };

  const renderedMenuSections = menuSections.map((menuSection) => {
    return (
      <span onClick={() => setMenuS} className="menu-section-item">
        {menuSection}
      </span>
    );
  });

  return (
    <div className="menu-container">
      <div className="menu-section">
          {renderedMenuSections}
      </div>
    </div>
  );
};

Menu.defaultProps = {
  menuSectionProp: MENU_CUISINE
};

export default Menu;
