import React, { useState } from "react";

import { MENU_CUISINE, MENU_SIDE, MENU_DRINK, MENU_DESSERT } from "./MenuSection";
import "./Menu.css";

const Menu = ({menuSection}) => {
  const [menuSectionState, setMenuSectionState] = useState(menuSection);

  const menuSelections = [
    MENU_CUISINE,
    MENU_SIDE,
    MENU_DRINK,
    MENU_DESSERT
  ];

  const renderedMenuSections = menuSelections.map((menuSelection) => {
    return (
      <li className="menu-selection-item">
        {menuSelection}
      </li>
    );
  });


  <div className="menu-container">
    <div className="menu-selection">
      <ul>
        {renderedMenuSelections}
      </ul>
    </div>
  </div>
};

Menu.defaultProps = {
  menuSection: MENU_CUISINE
};

export default Menu;
