import React, { useState } from "react";
import { Button } from "react-bootstrap";

import "./Cart.css";

const Cart = ({items, cart}) => {
  const [itemsState, setItems] = useState(items);
  const [cartState, setCart] = useState(cart);

  const findCartItemCounter = (item) => {
    return cart.find((cartItem) => item.id === cartItem.itemId).counter;
  };

  const changeCartItemCounter = (item, delta) => {
    const updatedCart = cart.map((cartItem) => {
      if (item.id === cartItem.id) {
        let newCount = cartItem.counter + delta;
        if (newCount < 1) {
          newCount = 1;
        }
        if (newCount > 9) {
          newCount = 9;
        }
        return {...cartItem, counter: newCount};
      }
      return cartItem;
    });
    setCart(updatedCart);
  };  

  const renderedItems = items.map((item) => {
    let itemCount = findCartItemCounter(item);
    return (

      <div key={item.id} className="item pad">
        <div className="img-box">
          <img src={item.imgSrc} alt=""/>
        </div>
        <div className="about">
          <h1 className="item-title">{item.itemName}</h1>
        </div>
        <div className="counter">
          <Button 
            onClick={() => changeCartItemCounter(item, 1)} 
            className={itemCount === 9 ? "item-btn item-btn-grey" : "item-btn item-btn-green item-btn-hover"}>
            +
          </Button>
        <div className="counter">{findCartItemCounter(item)}</div>
          <Button 
            onClick={() => changeCartItemCounter(item, -1)} 
            className={itemCount === 1 ? "item-btn item-btn-grey" : "item-btn item-btn-red itm-btn-hover"}>
            -
          </Button>
        </div>
        <div className="price">
          <div className="amount">${item.itemPrice}</div>
          <div className="remove"><u>Remove</u></div>
        </div>
      </div>
    );   
  });

  return (
    <div className="page-body">
      <div className="container">
        <div className="header">
          <h3 className="heading">Shopping Cart</h3>
          <h5 className="action">Remove All</h5>
        </div>        
        {renderedItems}
      </div>
    </div>
  );
};

export default Cart;
