  };

  return (
    <div className="menu-container">
      <div className="menu-section-types">
        {renderedMenuSectionTypeSelector}
      </div>
      <div>

      </div>
    </div>
  );
};

Menu.defaultProps = {
  menuSectionType: CUISINE
};

export default Menu;
