import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from "react-router-dom";

import NavBar from "./components/nav/NavBar";

/*

  Home: 
    Home page with logo, some "featured" items listed with links

  About:
    Introduce team members and talk about inspiration for project

  Login:
    User can login. If logged in, redirect to message page saying "already logged in".

  Register:
    User can register. If logged in, redirect to message page saying "to register new account, please logout".

  Account:
    User can view and edit profile. If not logged in, redirect to login page.

  Request reset pass:
    User can request password change. If logged in, just say "token sent to your email". If not logged in,
    prompt user for email address and then send token.

  Confirm reset pass:
    Requires email and token as params. (Email is used to query user in database.) If user and token associated
    with user are both found in db, then prompt user to and confirm new password.     

*/

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(true);

  let routes;

  if (isLoggedIn) {
    routes = (
      <Switch>

      </Switch>
    );
  } else {
    routes = (
      <Switch>

      </Switch>
    );
  }

  {/* For now just init routes same regardless if logged in or not */}
  routes = (
    <Switch>
      <Route path="/" exact>
        {/* Home */}
      </Route>
        <Route path="/about">
        {/* About */}
      </Route>
      <Route path="/login">
        {/* Login */}|
      </Route>
      <Route path="/register">
        {/* Register */}
      </Route>
      <Route path="/account">
        {/* Account */}
      </Route>
      <Route path="/request-reset-pass" exact>
        {/* Request reset pass */}
      </Route>  
      <Route path="/confirm-reset-pass/:email/:token" exact>
        {/* Confirm reset pass */}
      </Route>           
      <Route path="/item/:id" exact>
        {/* View item that user can put into cart */}
      </Route>
      <Route path="/cart">
        {/* Cart */}
      </Route>
      <Route path="/message">
        {/* Template page for showing any message */}
      </Route>
      <Redirect to="/login"/>
    </Switch>
  );

  return (
    <NavBar/>
  );
}

export default App;
