import React, { useState } from "react";
import { Button } from "react-bootstrap";

const Cart = ({items, cart}) => {
  const [itemsState, setItems] = useState(items);
  const [cartState, setCart] = useState(cart);

  const findCartItemCounter = (item) => {
    return cart.find((cartItem) => item.id === cartItem.itemId).counter;
  };

  const changeCartItemCounter = (item, delta) => {
    const updatedCart = cart.map((cartItem) => {
      if (item.id === cartItem.id) {
        let newCount = cartItem.counter + delta;
        if (newCount < 1) {
          newCount = 1;
        }
        if (newCount > 9) {
          newCount = 9;
        }
        return {...cartItem, counter: newCount};
      }
      return cartItem;
    });
    setCart(updatedCart);
  };  

  const renderedItems = items.map((item) => {
    // let itemCount = findCartItemCounter(item);
