        <div className="about">
          <h1 className="title">{item.itemName}</h1>
        </div>
        <div className="counter">
          <Button 
            onClick={() => changeCartItemCounter(item, 1)} 
            className={itemCount === 9 ? "item-btn item-btn-grey" : "item-btn item-btn-green item-btn-hover"}>
            +
          </Button>
        <div className="counter">{findCartItemCounter(item)}</div>
          <Button 
            onClick={() => changeCartItemCounter(item, -1)} 
            className={itemCount === 1 ? "item-btn item-btn-grey" : "item-btn item-btn-red itm-btn-hover"}>
            -
          </Button>
        </div>
        <div className="price">
          <div className="amount">${item.itemPrice}</div>
          <div className="remove"><u>Remove</u></div>
        </div>
      </div>
    );
    */
    return (
      <div></div>
    )
  });

  return (
    <div className="page-body">
      <div className="container">
        <div className="header">
          <h3 className="heading">Shopping Cart</h3>
          <h5 className="action">Remove All</h5>
        </div>
        {renderedItems}
      </div>
    </div>
  );
};

export default Cart;
