import React, { useState } from "react";
import { useParams } from "react-router-dom";

import Login from "./fragments/Login";
import Register from "./fragments/Register";

import { LOGIN, REQUEST_RESET_PASS, REGISTER, DO_RESET_PASS } from "./AuthMode";
import "./Auth.css";
import "../../shared/style/common.css";

const Auth = ({authmode}) => {
  const [mode, setMode] = useState(authmode);
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleLogin = (e) => {
    console.log("Clicked login button");
    e.preventDefault();
  };

  const handleRegister = (e) => {
    console.log("Clicked register button");
    e.preventDefault();
  };

  const handleRequestResetPass = (e) => {
    console.log("Click request reset pass");
    e.preventDefault();
  };

  const RenderRequestResetPass = () => {
    return (
     <div className="login-container">
        <form className="login-form">
          <div className="login-form-content">
            <h3 className="login-form-title">Reset Password</h3>
            <div className="text-center">
              Cancel resetting password?{" "}
              <span className="link-primary" onClick={() => setMode(LOGIN)}>
                Login
              </span>
            </div>
            <div className="form-group mt-3">
              <label>Email address</label>
              <input
                type="text"
                value={email}
                className="form-control mt-1"
                placeholder="Enter your email..."
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>                                                       
            <div className="d-grid gap-2 mt-3">
              <button className="button-primary button-primary-green" onClick={handleRequestResetPass}>
                Send Me a Reset Link
              </button>
            </div>
          </div>
        </form>
      </div>
    );
  };

  const RenderDoResetPass = () => {
    let {email, token} = useParams();
    console.log("Do request password page. Email: " + email + ". Token: " + token);

    return (
      <div>
        Do reset password
      </div>
    );
  };

  if (mode === LOGIN) {
    return <Login
        setMode={setMode}
        username={username}
        setUsername={setUsername}
        password={password}
        setPassword={setPassword}
        handleLogin={handleLogin}
      />;
  } else if (mode === REGISTER) {
    return <Register

      />;
  } else if (mode === REQUEST_RESET_PASS) {
    return <RenderRequestResetPass/>;
  } else if (mode === DO_RESET_PASS) {
    return <RenderDoResetPass/>;
  }
};

Auth.defaultProps = {
  authmode: LOGIN
};

export default Auth;
